  <script src="https://cdn.jsdelivr.net/npm/gsap@3.13.0/dist/gsap.min.js"></script>
  <script src="https://cdn.jsdelivr.net/npm/gsap@3.13.0/dist/DrawSVGPlugin.min.js"></script>
  <script src="https://cdn.jsdelivr.net/npm/gsap@3.13.0/dist/ScrollTrigger.min.js"></script>
  <script src="https://cdn.jsdelivr.net/npm/gsap@3.13.0/dist/ScrollSmoother.min.js"></script>
  <script src="https://cdn.jsdelivr.net/npm/gsap@3.12.5/dist/gsap.min.js"></script>
  <script src="https://cdn.jsdelivr.net/npm/gsap@3.12.5/dist/ScrollTrigger.min.js"></script>

  <script>
    document.addEventListener("DOMContentLoaded", () => {
      gsap.registerPlugin(DrawSVGPlugin, ScrollTrigger, ScrollSmoother);

      ScrollTrigger.normalizeScroll(true);

      const smoother = ScrollSmoother.create({
        wrapper: "#smooth-wrapper",
        content: "#smooth-content",
        smooth: 2,
        effects: true
      });

      ScrollTrigger.defaults({ scroller: "#smooth-content" });

      gsap.from('.draw', {
        drawSVG: "0%",
        ease: "expo.out",
        scrollTrigger: {
          trigger: '.heading',
          start: "clamp(top center)",
          scrub: true,
          pin: '.pin',
          pinSpacing: false,
        }
      });
    });

    gsap.registerPlugin(ScrollTrigger);

    gsap.utils.toArray(".anim-scale").forEach((el) => {
      gsap.to(".anim-scale", {
        opacity: 1,
        scale: 1,
        duration: 0.5,
        padding: 70,
        ease: "power3.out",
        stagger: 0.5, 
        scrollTrigger: {
          trigger: ".anim-section",
          start: "top 80%",  
          toggleActions: "play none none none"
        }
      });
    });

    const counters = document.querySelectorAll(".counter-count");

    counters.forEach(counter => {
      let finalValue = parseInt(counter.textContent);

      gsap.fromTo(counter,
          { innerText: 0, opacity: 0 },
          {
              innerText: finalValue,
              opacity: 1,
              duration: 2,
              ease: "power1.out",
              snap: { innerText: 1 },
              scrollTrigger: {
                  trigger: counter,
                  start: "top 85%",
                  toggleActions: "play none none none"
              }
          }
      );
    });
  </script>
